package geeksterHomwework;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		while (i<=10) {
		System.out.println(i);
		i=i+1;
		}
		
		int j=10;
		while (j>=1) {
		System.out.println(j);
		j=j-1;
		}
		
		int k=2;
		while (k<=10) {
		System.out.println(k);
		k=k+2;
		}
		
		int l=10;
		while (l>=1) {
		System.out.println(l);
		l=l-2;
		}


	}

}
